exports.getDate = function(){
    var completeDate = new Date();
    var h = completeDate.getHours();
    if (h<10) {
        h = "0" + h
    }
    var m = completeDate.getMinutes();
    if (m < 10) {
        m = "0" + m
    }
    var s = completeDate.getSeconds();
    if (s < 10) {
        s = "0" + s
    }
    var d = completeDate.getDate();
    if (d < 10) {
        d = "0" + d
    }
    var mo=completeDate.getMonth();
    if (mo < 10) {
        mo = "0" + mo
    }
    var y = completeDate.getFullYear();
    if (y < 10) {
        y = "0" + y
    }
    var date = "le " + d + "/" + mo + "/" + y + " Ã  " + h + ":" + m + ":" + s;

    return date;
}
